import jwt from "jsonwebtoken"
import { store } from "../../redux/store"

const getAuth = (token = false) => {
  const localStorageToken = `eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..yU8pFZZfmdylRlg6cZF47tLu9OeROdvFVjKVdO_xC3A`
  localStorage.setItem("token", localStorageToken)
  const localToken = localStorage.getItem("token") || localStorageToken

  if (localToken) {
    if (token) {
      return localToken
    }

    try {
      const decoded = jwt.decode(localToken)
      return decoded
    } catch (error) {
      console.log("jwt-decode", error)
      return null
    }
  }

  return null
}

const getAuthV3 = (token = false) => {
  const localToken = localStorage.getItem("accessTokenInternal")

  if (localToken) {
    if (token) {
      return localToken
    }

    try {
      const decoded = jwt.decode(localToken)
      console.log("jwt-decode", decoded)
      return decoded
    } catch (error) {
      console.log("jwt-decode", error)
      return null
    }
  }

  return null
}

const authCan = (arg = null) => {
  const user = store.getState()?.auth?.user || null

  if (!user) {
    return false
  }

  if (typeof arg === "function") {
    return arg(user)
  }

  if (typeof arg === "string") {
    // if (_.get(JSON.parse(user.Role.privilege), arg, null)) {
    //   return true
    // }
  }

  return false
}

export { getAuth, authCan, getAuthV3 }
